#!/usr/bin/env node

module.exports = function(ctx) {
  var shell = require('shelljs');
  var path = require('path');
  var fs = require('fs-extra');
  var ConfigParser = ctx.requireCordovaModule('cordova-common').ConfigParser,  
      config = new ConfigParser(path.join(ctx.opts.projectRoot, "config.xml")), 
      name = config.name();

  try {
    console.log(name);
    var envDelegateToEditor = path.join(ctx.opts.projectRoot, `/platforms/ios/${name}/Plugins/mobile-plugin-hybrid-init/EnvDelegate.m`);
    rmAlternativeContentSync(fs, envDelegateToEditor, /(\/\/ mobile-plugin-language import start)([\s\S]*\/\/ mobile-plugin-language import end)/);
    rmAlternativeContentSync(fs, envDelegateToEditor, /(\/\/ mobile-plugin-language application start)([\s\S]*\/\/ mobile-plugin-language application end)/);
    var envWebViewControllerToEditor = path.join(ctx.opts.projectRoot, `/platforms/ios/${name}/Plugins/mobile-plugin-web-container/EnvWebViewController.m`);
    rmAlternativeContentSync(fs, envWebViewControllerToEditor, /(\/\/ mobile-plugin-language import start)([\s\S]*\/\/ mobile-plugin-language import end)/);
    rmAlternativeContentSync(fs, envWebViewControllerToEditor, /(\/\/ mobile-plugin-language setcookie start)([\s\S]*\/\/ mobile-plugin-language setcookie end)/);
  } catch (err) {
    console.log(err);
  }
}

function rmAlternativeContentSync(fs, file, regularStr){
  var readFileData = fs.readFileSync(file, 'utf8');
  readFileData = readFileData.replace(regularStr, '');
  fs.writeFileSync(file, readFileData);
}
